  #{sort,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{createId,jdbcType=INTEGER}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateId,jdbcType=INTEGER}, #{remark,jdbcType=LONGVARCHAR}
  )
